-- Enrich SACCO metadata, seed Umurenge SACCO master data, and expose semantic search helpers
CREATE EXTENSION IF NOT EXISTS pg_trgm WITH SCHEMA public;

ALTER TABLE public.saccos
  ADD COLUMN IF NOT EXISTS sector TEXT,
  ADD COLUMN IF NOT EXISTS province TEXT,
  ADD COLUMN IF NOT EXISTS email TEXT,
  ADD COLUMN IF NOT EXISTS category TEXT,
  ADD COLUMN IF NOT EXISTS bnr_index INTEGER,
  ADD COLUMN IF NOT EXISTS search_slug TEXT;

-- Backfill existing rows (if any) so constraints can be applied safely
UPDATE public.saccos
SET
  sector = COALESCE(sector, sector_code),
  province = COALESCE(province, district),
  category = COALESCE(category, 'Deposit-Taking Microfinance Cooperative (UMURENGE SACCO)'),
  search_slug = COALESCE(search_slug, trim(both '-' from lower(regexp_replace(name, '[^a-z0-9]+', '-', 'g'))))
WHERE sector IS NULL OR province IS NULL OR category IS NULL OR search_slug IS NULL;

ALTER TABLE public.saccos
  ALTER COLUMN sector SET NOT NULL,
  ALTER COLUMN province SET NOT NULL,
  ALTER COLUMN category SET NOT NULL;

-- Add generated semantic search document and supporting indexes
DO $$
BEGIN
  IF NOT EXISTS (
    SELECT 1
    FROM information_schema.columns
    WHERE table_schema = 'public'
      AND table_name = 'saccos'
      AND column_name = 'search_document'
  ) THEN
    ALTER TABLE public.saccos
      ADD COLUMN search_document tsvector GENERATED ALWAYS AS (
        setweight(to_tsvector('simple', COALESCE(name, '')), 'A') ||
        setweight(to_tsvector('simple', COALESCE(sector, '')), 'B') ||
        setweight(to_tsvector('simple', COALESCE(district, '')), 'B') ||
        setweight(to_tsvector('simple', COALESCE(province, '')), 'C') ||
        setweight(to_tsvector('simple', COALESCE(email, '')), 'D')
      ) STORED;
  END IF;
END $$;

CREATE UNIQUE INDEX IF NOT EXISTS saccos_bnr_index_unique ON public.saccos(bnr_index);
CREATE UNIQUE INDEX IF NOT EXISTS saccos_sector_code_unique ON public.saccos(sector_code);
CREATE INDEX IF NOT EXISTS saccos_search_document_idx ON public.saccos USING GIN(search_document);
CREATE INDEX IF NOT EXISTS saccos_name_trgm_idx ON public.saccos USING GIN (name gin_trgm_ops);
CREATE INDEX IF NOT EXISTS saccos_sector_trgm_idx ON public.saccos USING GIN (sector gin_trgm_ops);
CREATE INDEX IF NOT EXISTS saccos_search_slug_idx ON public.saccos(search_slug);

-- Prefer sanitized parameters when building semantic queries
CREATE OR REPLACE FUNCTION public.search_saccos(
  query TEXT,
  limit_count INTEGER DEFAULT 20,
  district_filter TEXT DEFAULT NULL,
  province_filter TEXT DEFAULT NULL
)
RETURNS TABLE (
  id UUID,
  name TEXT,
  sector TEXT,
  district TEXT,
  province TEXT,
  email TEXT,
  category TEXT,
  bnr_index INTEGER,
  similarity_score NUMERIC,
  rank_score NUMERIC
)
LANGUAGE sql
SECURITY INVOKER
SET search_path = public
AS $$
  WITH params AS (
    SELECT
      NULLIF(trim(query), '') AS sanitized_query,
      NULLIF(trim(district_filter), '') AS district_like,
      NULLIF(trim(province_filter), '') AS province_like,
      LEAST(GREATEST(COALESCE(limit_count, 20), 1), 100) AS limit_size
  ), expanded AS (
    SELECT
      params.limit_size,
      params.district_like,
      params.province_like,
      params.sanitized_query,
      CASE
        WHEN params.sanitized_query IS NULL THEN NULL
        ELSE websearch_to_tsquery('simple', params.sanitized_query)
      END AS ts_query
    FROM params
  ), ranked AS (
    SELECT
      s.id,
      s.name,
      s.sector,
      s.district,
      s.province,
      s.email,
      s.category,
      s.bnr_index,
      expanded.sanitized_query,
      expanded.ts_query,
      CASE WHEN expanded.sanitized_query IS NULL THEN 0 ELSE similarity(s.name, expanded.sanitized_query) END AS trigram_name,
      CASE
        WHEN expanded.sanitized_query IS NULL THEN 0
        ELSE similarity(COALESCE(s.sector, '') || ' ' || COALESCE(s.district, ''), expanded.sanitized_query)
      END AS trigram_location,
      CASE
        WHEN expanded.ts_query IS NULL THEN 0
        ELSE ts_rank(s.search_document, expanded.ts_query)
      END AS ts_rank_score
    FROM public.saccos s
    CROSS JOIN expanded
    WHERE (
      expanded.sanitized_query IS NULL
      OR (
        (expanded.ts_query IS NOT NULL AND s.search_document @@ expanded.ts_query)
        OR (
          expanded.sanitized_query IS NOT NULL
          AND similarity(s.name, expanded.sanitized_query) > 0.1
        )
        OR (
          expanded.sanitized_query IS NOT NULL
          AND similarity(COALESCE(s.sector, '') || ' ' || COALESCE(s.district, ''), expanded.sanitized_query) > 0.1
        )
      )
    )
      AND (expanded.district_like IS NULL OR s.district ILIKE expanded.district_like)
      AND (expanded.province_like IS NULL OR s.province ILIKE expanded.province_like)
  )
  SELECT
    id,
    name,
    sector,
    district,
    province,
    email,
    category,
    bnr_index,
    GREATEST(trigram_name, trigram_location) AS similarity_score,
    ts_rank_score + GREATEST(trigram_name, trigram_location) AS rank_score
  FROM ranked
  ORDER BY rank_score DESC, similarity_score DESC, bnr_index ASC
  LIMIT (SELECT limit_size FROM expanded LIMIT 1)
$$;

